/**
 * üî• Makandal Chrome Recorder - Template Intelligent
 * ü§ñ Template de base pour hydratation par IA
 * 
 * Variables √† remplacer par l'IA selon le projet :
 * {{PROJECT_NAME}} - Nom du projet (ex: "Wedia Portal", "React App")
 * {{PROJECT_DESCRIPTION}} - Description courte du projet
 * {{SELECTOR_PRIORITIES}} - Array des s√©lecteurs prioritaires  
 * {{DYNAMIC_VALUE_PATTERNS}} - Patterns des valeurs dynamiques √† √©viter
 * {{DYNAMIC_CLASS_PATTERNS}} - Patterns des classes dynamiques √† √©viter
 * {{SPECIFIC_LOGIC}} - Logique sp√©cifique au projet
 * {{MESSAGES}} - Messages console personnalis√©s
 * {{TEST_ELEMENTS}} - √âl√©ments typiques pour la fonction test()
 * {{GENERATION_DATE}} - Date de g√©n√©ration
 * {{PROJECT_SPECIFIC_FUNCTIONS}} - Fonctions sp√©cialis√©es au projet
 */
(function() {
  'use strict';
  
  // IA: Remplace "Wedia Portal Dev" par le nom du projet
  console.log('üî• Chargement Makandal - Wedia Portal Dev...');

  // üéØ Configuration sp√©cialis√©e (IA: remplace les valeurs ci-dessous)
  const PROJECT_CONFIG = {
    // IA: Remplace ces valeurs
    name: 'Wedia Portal Dev - G√©n√©r√© par IA le 2025-09-15 00:43:38',
    description: 'Configuration d√©veloppement avec debug activ√©',
    
    // IA: Remplace par array des s√©lecteurs prioritaires du projet
    selectorPriorities: [
      "portal-action",
      "data-portal",
      "data-testid",
      "data-automation",
      "data-debug",
      "aria-label",
      "id",
      "class"
],
    
    // IA: Remplace par patterns sp√©cifiques au projet
    dynamicValuePatterns: [
      {
            "pattern": "^__BVID__",
            "description": "Bootstrap Vue IDs dynamiques",
            "flags": ""
      },
      {
            "pattern": "^\\d+$",
            "description": "IDs purement num√©riques",
            "flags": ""
      }
],
    
    // IA: Remplace par classes dynamiques du projet
    dynamicClassPatterns: [
      {
            "pattern": "^b-\\w+-\\d+$",
            "description": "Bootstrap Vue classes",
            "flags": ""
      }
],
    
    // Pr√©f√©rences par d√©faut (IA: ajuste selon besoin)
    preferences: {
      allowTextSelectors: true,
      maxTextLength: 40,
      ignoreHiddenElements: false,
      preferDataAttributes: true,
      projectSpecific: {
        // IA: Ajoute propri√©t√©s sp√©cifiques ici
        detectFrameworkComponents: true
      }
    }
  };

  // üß† Compilation des patterns pour performance
  let compiledValuePatterns = [];
  let compiledClassPatterns = [];
  
  function compilePatterns() {
    // Compilation des regex depuis la configuration
    compiledValuePatterns = PROJECT_CONFIG.dynamicValuePatterns.map(p => 
      new RegExp(p.pattern, p.flags || '')
    );
    
    compiledClassPatterns = PROJECT_CONFIG.dynamicClassPatterns.map(p => 
      new RegExp(p.pattern, p.flags || '')
    );
    
    console.log('‚úÖ Patterns {{PROJECT_NAME}} compil√©s:', {
      valeurs: compiledValuePatterns.length,
      classes: compiledClassPatterns.length
    });
  }

  // üéØ Fonction principale - G√©n√©ration s√©lecteur stable
  function generateStableSelector(element) {
    if (!element || !element.nodeType) {
      console.warn('‚ö†Ô∏è √âl√©ment invalide fourni √† generateStableSelector');
      return null;
    }

    // Ignorer √©l√©ments cach√©s si configur√©
    if (PROJECT_CONFIG.preferences.ignoreHiddenElements) {
      if (element.offsetParent === null || 
          window.getComputedStyle(element).display === 'none') {
        return null;
      }
    }

    // 1Ô∏è‚É£ Priorit√© aux attributs m√©tier projet
    for (const attr of PROJECT_CONFIG.selectorPriorities) {
      const value = element.getAttribute(attr);
      if (value && !isDynamicValue(value)) {
        // IA: Ajoute logique sp√©cifique au projet ici
        
        // Logique sp√©cifique Portal
        if (attr === 'portal-action') {
          console.log('üèõÔ∏è Portal Action:', value);
          return `[portal-action="${value}"]`;
        }
        if (attr === 'data-portal') {
          console.log('üéØ Data Portal:', value);
          return `[data-portal="${value}"]`;
        }
        if (attr === 'data-testid') {
          console.log('üéØ Test ID trouv√©:', value);
        }
        return `[${attr}="${value}"]`;
      }
    }

    // 2Ô∏è‚É£ Classes CSS stables (filtrage projet)
    const classList = Array.from(element.classList)
      .filter(cls => !isDynamicClass(cls))
      .filter(cls => isProjectStableClass(cls));
    
    if (classList.length > 0) {
      const classSelector = `.${classList.join('.')}`;
      console.log('üé® S√©lecteur classes stables:', classSelector);
      return classSelector;
    }

    // 3Ô∏è‚É£ S√©lecteur par texte (optimis√© projet)
    if (PROJECT_CONFIG.preferences.allowTextSelectors) {
      const textContent = element.textContent?.trim();
      const maxLength = PROJECT_CONFIG.preferences.maxTextLength;
      
      if (textContent && 
          textContent.length <= maxLength && 
          isUniqueText(textContent) &&
          isProjectRelevantText(textContent)) {
        console.log('üìù S√©lecteur texte projet:', textContent);
        return `text/${textContent}`;
      }
    }

    // 4Ô∏è‚É£ Fallback intelligent projet
    return generateProjectFallback(element);
  }

  // üö´ D√©tection valeurs dynamiques  
  function isDynamicValue(value) {
    return compiledValuePatterns.some(pattern => pattern.test(value));
  }

  // üö´ D√©tection classes dynamiques
  function isDynamicClass(className) {
    return compiledClassPatterns.some(pattern => pattern.test(className));
  }

  // ‚úÖ Classes stables sp√©cifiques projet (IA: remplace cette logique)
  function isProjectStableClass(className) {
    // IA: Remplace par logique sp√©cifique au projet
    const stablePatterns = [
      /^btn/,           // Boutons
      /^form/,          // Formulaires
      /^nav/,           // Navigation
      /^card/,          // Cartes
      /^modal/,         // Modales
      /^alert/          // Alertes
    ];
    return stablePatterns.some(pattern => pattern.test(className));
  }

  // üìù Validation texte pertinent projet (IA: remplace cette logique)
  function isProjectRelevantText(text) {
    // IA: Remplace par validation sp√©cifique au projet
    const irrelevantPatterns = [
      /^\d+$/,          // Nombres seuls
      /^[A-Z]{1,3}$/,   // Acronymes courts
      /^\s*$/,          // Espaces
      /^\.{3,}$/,       // Points de suspension
      /^-+$/            // Tirets
    ];
    return !irrelevantPatterns.some(pattern => pattern.test(text));
  }

  // üîç V√©rification unicit√© texte
  function isUniqueText(text) {
    try {
      const elements = document.querySelectorAll('*');
      let count = 0;
      
      for (const el of elements) {
        if (el.textContent?.trim() === text) {
          count++;
          if (count > 1) return false;
        }
      }
      
      return count === 1;
    } catch (error) {
      console.warn('‚ö†Ô∏è Erreur v√©rification unicit√© texte:', error);
      return false;
    }
  }

  // üÜò Fallback intelligent projet (IA: remplace cette logique)
  function generateProjectFallback(element) {
    // IA: Remplace par logique fallback sp√©cifique au projet
    const tagName = element.tagName.toLowerCase();
    
    switch (tagName) {
      case 'button':
        const type = element.type;
        if (type && type !== 'button') return `button[type="${type}"]`;
        return 'button';
        
      case 'input':
        const inputType = element.type || 'text';
        return `input[type="${inputType}"]`;
        
      case 'a':
        const href = element.href;
        if (href && href.length < 50 && !href.includes('javascript:')) {
          return `a[href="${href}"]`;
        }
        return 'a';
        
      default:
        return tagName;
    }
  }

  // Fonctions sp√©cialis√©es projet (IA: ajoute fonctions sp√©cifiques ici)
  function detectProjectComponent(element) {
    // IA: Remplace par d√©tection sp√©cifique au projet
    return element.className.match(/project-[\w-]+/)?.[0] || null;
  }

  // üß™ Fonction de test pour validation
  function test() {
    console.log('üß™ Test Makandal pour projet...');
    
    // Test sur √©l√©ments typiques projet (IA: remplace cette liste)
    const testSelectors = [
      'data-testid',
      'aria-label',
      '.btn',
      '.form-control',
      'button[type="submit"]'
    ];
    
    let found = 0;
    testSelectors.forEach(selector => {
      try {
        const element = document.querySelector(`[${selector}], ${selector}`);
        if (element) {
          const result = generateStableSelector(element);
          console.log(`‚úÖ ${selector} ‚Üí ${result}`);
          found++;
        }
      } catch (e) {
        console.log(`‚ö†Ô∏è ${selector} ‚Üí non trouv√©`);
      }
    });
    
    console.log(`üéØ Test termin√©: ${found}/${testSelectors.length} s√©lecteurs trouv√©s`);
    return found;
  }

  // üöÄ Initialisation automatique
  function init() {
    compilePatterns();
    
    // Messages personnalis√©s projet (IA: remplace ces messages)
    console.log('üõ†Ô∏è Makandal Portal DEV - Mode debug activ√© !');
    console.log('üìã S√©lecteurs prioritaires:', PROJECT_CONFIG.selectorPriorities.slice(0, 4));
    console.log('üö´ Patterns dynamiques d√©tect√©s:', {
      valeurs: PROJECT_CONFIG.dynamicValuePatterns.length,
      classes: PROJECT_CONFIG.dynamicClassPatterns.length
    });
    
    // Export global pour utilisation
    window.MakandalRecorder = {
      generateStableSelector,
      isDynamicValue,
      isDynamicClass,
      isProjectStableClass,
      CONFIG: PROJECT_CONFIG,
      test,
      detectProjectComponent,
      version: '2.0.0-template', // IA: remplace par date g√©n√©ration
      project: 'Wedia Portal Dev' // IA: remplace par nom projet
    };
    
    console.log('üíª Chrome Recorder Portal DEV pr√™t !');
    console.log('üí° Tapez MakandalRecorder.test() pour valider');
  }

  // üé¨ Auto-initialisation
  init();

})();

// üéâ Script Makandal pour projet - Template hydrat√© par IA
console.log('‚öîÔ∏è "Fran√ßois Makandal guide l\'optimisation de vos s√©lecteurs !" ‚öîÔ∏è');
